    public static void sortWithMultipleThreads(List<Integer> list) throws ExecutionException, InterruptedException {
        long startTime = System.currentTimeMillis();
        ExecutorService executorService = Executors.newCachedThreadPool();
        MergeSorter mergeSorter = new MergeSorter(list, executorService);
        Future<List<Integer>> sortedListFuture = executorService.submit(mergeSorter);
        List<Integer> sortedList = sortedListFuture.get();
        long endTime = System.currentTimeMillis();
        long elapsedTime = endTime - startTime;
        System.out.println(sortedList);
        System.out.println(elapsedTime);
        executorService.shutdown();
    }

    public static void sortWithASinguleThread(List<Integer> list) throws ExecutionException, InterruptedException {
        long startTime = System.currentTimeMillis();
        MergeSorterSingleThread mergeSorterSingleThread = new MergeSorterSingleThread(list);
        List<Integer> sortedList = mergeSorterSingleThread.merge();
        long endTime = System.currentTimeMillis();
        long elapsedTime = endTime - startTime;
        System.out.println(sortedList);
        System.out.println(elapsedTime);
    }
